import React, { useState } from "react";
import "./App.css";
import PersonalityForm from "./components/PersonalityForm";
import "./components/PersonalityForm.css";

function App() {
  const [userProfile, setUserProfile] = useState(null);

  const handlePersonalitySubmit = (personality) => {
    setUserProfile(personality);
    console.log("Personality submitted:", personality);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Chart My Friends</h1>
        <p>Myers-Briggs Compatibility Chart</p>
      </header>

      <main className="App-main">
        {!userProfile ? (
          <PersonalityForm onSubmit={handlePersonalitySubmit} />
        ) : (
          <div className="profile-display">
            <h2>Welcome, {userProfile.name}!</h2>
            <p>Your personality profile has been created.</p>
            <div className="profile-stats">
              <p>
                Introversion: {userProfile.introvert}% | Extroversion:{" "}
                {userProfile.extrovert}%
              </p>
              <p>
                Intuition: {userProfile.intuitive}% | Sensing:{" "}
                {userProfile.sensing}%
              </p>
              <p>
                Thinking: {userProfile.thinking}% | Feeling:{" "}
                {userProfile.feeling}%
              </p>
              <p>
                Judging: {userProfile.judging}% | Perceiving:{" "}
                {userProfile.perceiving}%
              </p>
            </div>
            <button onClick={() => setUserProfile(null)} className="reset-btn">
              Create New Profile
            </button>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
