import React, { useState } from "react";

const PersonalityForm = ({ onSubmit }) => {
  const [personality, setPersonality] = useState({
    name: "",
    introvert: 50,
    extrovert: 50,
    intuitive: 50,
    sensing: 50,
    thinking: 50,
    feeling: 50,
    judging: 50,
    perceiving: 50,
  });

  const handleInputChange = (trait, value) => {
    const numValue = parseInt(value);
    setPersonality((prev) => ({
      ...prev,
      [trait]: numValue,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(personality);
  };

  const traitPairs = [
    {
      left: "introvert",
      right: "extrovert",
      label: "Introversion vs Extroversion",
    },
    { left: "intuitive", right: "sensing", label: "Intuition vs Sensing" },
    { left: "thinking", right: "feeling", label: "Thinking vs Feeling" },
    { left: "judging", right: "perceiving", label: "Judging vs Perceiving" },
  ];

  return (
    <div className="personality-form">
      <h2>Create Your Personality Profile</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Your Name:</label>
          <input
            type="text"
            id="name"
            value={personality.name}
            onChange={(e) =>
              setPersonality((prev) => ({ ...prev, name: e.target.value }))
            }
            required
            placeholder="Enter your name"
          />
        </div>

        {traitPairs.map(({ left, right, label }) => (
          <div key={left} className="trait-pair">
            <h3>{label}</h3>
            <div className="slider-container">
              <div className="trait-label">
                <span>{left.charAt(0).toUpperCase() + left.slice(1)}</span>
                <span>{personality[left]}%</span>
              </div>
              <input
                type="range"
                min="0"
                max="100"
                value={personality[left]}
                onChange={(e) => {
                  const leftValue = parseInt(e.target.value);
                  const rightValue = 100 - leftValue;
                  setPersonality((prev) => ({
                    ...prev,
                    [left]: leftValue,
                    [right]: rightValue,
                  }));
                }}
                className="trait-slider"
              />
              <div className="trait-label">
                <span>{right.charAt(0).toUpperCase() + right.slice(1)}</span>
                <span>{personality[right]}%</span>
              </div>
            </div>
          </div>
        ))}

        <button type="submit" className="submit-btn">
          Create Profile
        </button>
      </form>
    </div>
  );
};

export default PersonalityForm;
